import ReactMarkdown from 'react-markdown'
import Layout from '../../components/Layout'
import fetch from 'isomorphic-unfetch'
import Router from 'next/router'

async function publish(id) {
  const res = await fetch(`http://localhost:3000/api/publish/${id}`, {
    method: 'PUT',
  })
  const data = await res.json()
  await Router.push('/')
}

async function destroy(id) {
  const res = await fetch(`http://localhost:3000/api/post/${id}`, {
    method: 'DELETE',
  })
  const data = await res.json()
  Router.push('/')
}

const Cameras = props => {
  let cameraname = props.cameraname
  if (!props.published) {
    cameraname = `${cameraname} (Draft)`
  }
  return (
    <Layout>
      <div className="page">
        <h2>{cameraname}</h2>
        <ReactMarkdown source={props.serialnumber} />
        <div className="actions">
          {!props.published && (
            <button onClick={() => publish(props.id)}>Publish</button>
          )}
          <button onClick={() => destroy(props.id)}>Delete</button>
        </div>
      </div>
      <style jsx>{`
        .page {
          background: white;
          padding: 2rem;
        }

        .actions {
          margin-top: 2rem;
        }

        button {
          background: #ececec;
          border: 0;
          border-radius: 0.125rem;
          padding: 1rem 2rem;
        }

        button + button {
          margin-left: 1rem;
        }
      `}</style>
    </Layout>
  )
}

export const getServerSideProps = async (context) => {
  const res = await fetch(`http://localhost:3000/api/post/${context.params.id}`)
  const data = await res.json()
  return {props: {...data}}
}

export default Post
