import fetch from 'isomorphic-unfetch'
import Layout from '../components/Layout'
import Post from '../components/Post'


async function handleDELETE(postId, res) {
  const post = await prisma.cameras.delete({
    where: { id: Number(postId) },
  })
  res.json(post)
}
async function destroy(id) {
  const res = await fetch(`http://localhost:3000/api/post/${id}`, {
    method: 'DELETE',
  })
  const data = await res.json()
  Router.push('/')
}

const drafts = props => {
  return (
    <Layout>
      <div className="page">
        <h1>Listagem</h1>
        <main>
          {props.drafts.map(cameras => (
            <div key={cameras.id} className="cameras">
              <Post cameras={cameras} />
              <button onClick={() => handleDELETE(props.id)}>Delete</button>
            </div>
          ))}
        </main>
      </div>
      <style jsx>{`
        .cameras {
          background: white;
          transition: box-shadow 0.1s ease-in;
        }

        .cameras:hover {
          box-shadow: 1px 1px 3px #aaa;
        }

        .cameras + .cameras {
          margin-top: 2rem;
        }
      `}</style>
    </Layout>
  )
}

export const getServerSideProps = async () => {
  const res = await fetch('http://localhost:3000/api/drafts')
  const drafts = await res.json()
  return {
    props : { drafts },
  }
}

export default drafts
